START TRANSACTION;
USE `gameOfThrones`;
INSERT INTO `gameOfThrones`.`great_houses` (`gh_id`, `gh_name`, `gh_desc`) VALUES (1, 'Stark', 'House Stark rules the North of Westeros from their seat in Winterfell. Their sigil is a grey direwolf on a white field');
INSERT INTO `gameOfThrones`.`great_houses` (`gh_id`, `gh_name`, `gh_desc`) VALUES (2, 'Arryn', 'House Arryn of the Eyrie rules over the Vale of Arryn. The Eyrie is the acnestral seat of the Mountain Kings. Their sigil is a white crescent moon and falcon on a blue field.');
INSERT INTO `gameOfThrones`.`great_houses` (`gh_id`, `gh_name`, `gh_desc`) VALUES (3, 'Frey', 'House Frey rules the Riverlands from the castle of Riverrun. Betrayed House Stark to the Lannisters');
INSERT INTO `gameOfThrones`.`great_houses` (`gh_id`, `gh_name`, `gh_desc`) VALUES (4, 'Greyjoy', 'House Greyjoy rules the Iron Islands from the castle of Pyke');
INSERT INTO `gameOfThrones`.`great_houses` (`gh_id`, `gh_name`, `gh_desc`) VALUES (5, 'Lannister', 'House Lannister is the current royal house of the Seven Kingdoms and rules from the Red Keep in King\'s Landing. They also rule the Westerlands from the castle of Casterly Rock.');
INSERT INTO `gameOfThrones`.`great_houses` (`gh_id`, `gh_name`, `gh_desc`) VALUES (6, 'Targaryen', 'House Targaryen is the claimant royal house of the Sevent Kingdoms, and former royal house and rulers of the Crownlands from the Red Keep. Current lead by Daenerys Targaryen');
INSERT INTO `gameOfThrones`.`great_houses` (`gh_id`, `gh_name`, `gh_desc`) VALUES (7, 'Tully', 'House Tully are the former rulers of the Riverlands ruling from Riverrun until the events of the Red Wedding.');
INSERT INTO `gameOfThrones`.`great_houses` (`gh_id`, `gh_name`, `gh_desc`) VALUES (8, 'Baratheon', 'House Baratheon were the rulers of the Crownlands and Stormlands from the Red Keep and Storm\'s End, respectively. Currently extinct due to the main bloodline members being dead.');
INSERT INTO `gameOfThrones`.`great_houses` (`gh_id`, `gh_name`, `gh_desc`) VALUES (9, 'Martell', 'House Martell were rulers of Dorne from the Castle of Sunspear. Currently extinct since the main bloodline was killed by Ellaria Sand and other members of the Sand Snakes in their coup');
INSERT INTO `gameOfThrones`.`great_houses` (`gh_id`, `gh_name`, `gh_desc`) VALUES (10, 'Bolton', 'House Bolton were formerly bannermen of House Stark however after the Red Wedding became the rulers of the North from the castle of Winterfell. However the sole heir was killed in the aftermath of the Battle of the Bastards making the house extinct.');
INSERT INTO `gameOfThrones`.`great_houses` (`gh_id`, `gh_name`, `gh_desc`) VALUES (11, 'Tyrell', 'House Tyrell of Highgarden ruled over the Reach, a vast, fertile, and heavily-populated region of southwestern Westeros, from their castle-seat of Highgarden as Lords Paramount of the Reach and Wardens of the South after taking control of the region from House Gardener during the Targaryen conquest. Their sigil is a golden rose on a green field.');

COMMIT;


