START TRANSACTION;
USE `gameOfThrones`;
INSERT INTO `gameOfThrones`.`region` (`region_id`, `region_name`, `region_desc`, `controlled_by_gh`) VALUES (1, 'The North', 'The largest of the nine major regions of Westeros. According to legend, the Kingdom of the North was established 8000 years ago by Bran the Builder, founder of House Stark.', Stark);
INSERT INTO `gameOfThrones`.`region` (`region_id`, `region_name`, `region_desc`, `controlled_by_gh`) VALUES (2, 'The Vale of Arryn', 'One of the consituent regions of the Seven Kingdoms. Before the Targaryen Conquest, it was a sovereign nation known as the Kingdom of Mountain and Vale. It is surrounded and protected by the Moutains of the Moon and isolated from the rest of Westeros and only accessible during warmer seasons.', Arryn);
INSERT INTO `gameOfThrones`.`region` (`region_id`, `region_name`, `region_desc`, `controlled_by_gh`) VALUES (3, 'The Reach', 'One of the administrative regions of the Seven Kingdoms and was formerly a sovereign nation known as the Kingdom of the Reach before the Targaryen conquest. Second largest region in the Seven Kingdoms behind the North', Tyrell);
INSERT INTO `gameOfThrones`.`region` (`region_id`, `region_name`, `region_desc`, `controlled_by_gh`) VALUES (4, 'Dorne', 'One of the nine consituent regions of the Seven Kingdoms, and the southernmost part of Westeros.', Martell);
INSERT INTO `gameOfThrones`.`region` (`region_id`, `region_name`, `region_desc`, `controlled_by_gh`) VALUES (5, 'The Riverlands', 'Located roughly in the center of the continent, the Riverlands are one of the constituent regions of the Seven Kingdoms. They have been a frequent battleground in the civil wars that have afflicted the Seven Kingdoms.', Frey);
INSERT INTO `gameOfThrones`.`region` (`region_id`, `region_name`, `region_desc`, `controlled_by_gh`) VALUES (6, 'Beyond the Wall', 'This is the large area of Westeros that lies North beyond the Wall. It is inhabited by tribes known as the Free Folk or Wildlings.', NULL);
INSERT INTO `gameOfThrones`.`region` (`region_id`, `region_name`, `region_desc`, `controlled_by_gh`) VALUES (7, 'The Stormlands', 'Located in the south east coast of the Seven Kingdoms of Westeros, the Stormlands are one of the constituent regions of the Seven Kingdoms. It is named for the savage and frequent storms from the Narrow Sea that batter the coast.', Baratheon);
INSERT INTO `gameOfThrones`.`region` (`region_id`, `region_name`, `region_desc`, `controlled_by_gh`) VALUES (8, 'Iron Islands', 'A group of seven small rocky islands clustered off the western coast of the mainland of Westeros. They are the smallest and least populated region of the continent however the naval skills of their population is unmatched.', Greyjoy);
INSERT INTO `gameOfThrones`.`region` (`region_id`, `region_name`, `region_desc`, `controlled_by_gh`) VALUES (9, 'The Free Cities', 'Former colonies of the Valyrian Freehold before its fall, each city is now an independent city-state, competing amongst themselves for land and resources', NULL);
INSERT INTO `gameOfThrones`.`region` (`region_id`, `region_name`, `region_desc`, `controlled_by_gh`) VALUES (10, 'Dothraki Sea', 'The vast grasslands of the interior of Essos. It is inhabited by the Dothraki whose capital is Vaes Dothrak', NULL);
INSERT INTO `gameOfThrones`.`region` (`region_id`, `region_name`, `region_desc`, `controlled_by_gh`) VALUES (11, 'The Crownlands', 'One of the nine constituent regions of the Seven Kingdoms. Ruled directly by the monarch on the Iron Throne from the city of King\'s Landing.', Lannister);
INSERT INTO `gameOfThrones`.`region` (`region_id`, `region_name`, `region_desc`, `controlled_by_gh`) VALUES (12, 'Slaver\'s Bay', 'Located on the souther coast of Essos, the cities of Slaver\'s Bay were the hub of the international slave trade for millennia.', NULL);

COMMIT;


