INSERT INTO `gameOfThrones`.`person_of_interest` (`poi_id`, `poi_fname`, `poi_lname`, `poi_desc`, `poi_age`, `poi_titles`, `gh_id`, `lh_id`, `org_id`) VALUES (4, 'Arya', 'Stark', 'The youngest daughter of the Stark House. After escaping the persecution of House Stark by the Lannisters, she is trained as a Faceless Man in Braavos.', 17, 'Princess', 1, NULL, NULL);
INSERT INTO `gameOfThrones`.`person_of_interest` (`poi_id`, `poi_fname`, `poi_lname`, `poi_desc`, `poi_age`, `poi_titles`, `gh_id`, `lh_id`, `org_id`) VALUES (5, 'Tyrion', 'Lannister', 'Youngest child and a dwarf of House Lannister. Uses his wit and intellect to overcome many situations. Falsely accused of murdering King Joffrey. Murdered his father Tywin before fleeing to Essos.', 39, 'Lord of Casterly Rock, Lord Paramount of the Westerlands, Warden of the West, Hand of the Queen, Master of Coin, Acting Hand of the King', 5, NULL, 4);
INSERT INTO `gameOfThrones`.`person_of_interest` (`poi_id`, `poi_fname`, `poi_lname`, `poi_desc`, `poi_age`, `poi_titles`, `gh_id`, `lh_id`, `org_id`) VALUES (6, 'Tywin', 'Lannister', 'Head of House Lannister before being killed by Tyrion. Ruled the Westerlands with an Iron Fist. ', 67, 'Lord of Casterly Rock, Lord Paramount of the Westerlands, Warden of the West, Shield of Lannisport, Hand of the King, Protector of the Realm, Savior of the City, Ser', 5, NULL, NULL);
INSERT INTO `gameOfThrones`.`person_of_interest` (`poi_id`, `poi_fname`, `poi_lname`, `poi_desc`, `poi_age`, `poi_titles`, `gh_id`, `lh_id`, `org_id`) VALUES (7, 'Jaime ', 'Lannister', 'Eldest son of House Lannister, involved in an incestuous relationship with Cersei Lannister and the biological father to Joffrey, Myrcella, and Tommen', 43, 'Ser, Lord Commander of the Kingsguard', 5, NULL, 6);
INSERT INTO `gameOfThrones`.`person_of_interest` (`poi_id`, `poi_fname`, `poi_lname`, `poi_desc`, `poi_age`, `poi_titles`, `gh_id`, `lh_id`, `org_id`) VALUES (8, 'Cersei ', 'Lannister', 'Eldest daughter of House Lannister, widow of King Robert Baratheon, assumed the Throne after the deaths of Joffrey, Myrcella, and Tommen', 43, 'Queen of the Andals and the First Men, Protector of the Seven Kingdoms, Lady of Casterly Rock, Lady Paramount of the Westerlands, Wardeness of the West, Queen Mother, Queen Regent, Queen Consort', 5, NULL, NULL);
INSERT INTO `gameOfThrones`.`person_of_interest` (`poi_id`, `poi_fname`, `poi_lname`, `poi_desc`, `poi_age`, `poi_titles`, `gh_id`, `lh_id`, `org_id`) VALUES (9, 'Joffrey', 'Baratheon', 'Supposed eldest son of Robert Baratheon and Cersei Lannister (actually son of Jaime and Cersei). Spoiled, arrogant, cowardly, childish and sadistic are traits that describe him and become clear when he ascends to the throne.', 19, 'King of the Andals and the First Men, Lord of the Seven Kingdoms, Protector of the Realm, Lord of Storm\'s End, Lord Paramount of the Stormlands', 8, NULL, NULL);
INSERT INTO `gameOfThrones`.`person_of_interest` (`poi_id`, `poi_fname`, `poi_lname`, `poi_desc`, `poi_age`, `poi_titles`, `gh_id`, `lh_id`, `org_id`) VALUES (10, 'Robert ', 'Baratheon', 'Former ruler of the Seven Kingdoms. Overthrew the Targaryens in Robert\'s Rebellion.', NULL, 'King of the Andals and the First Men, Lord of the Seven Kingdoms, Protector of the Realm, Lord of Storm\'s End, Lord Paramount of the Stormlands', 8, NULL, NULL);
