INSERT INTO `gameOfThrones`.`person_of_interest` (`poi_id`, `poi_fname`, `poi_lname`, `poi_desc`, `poi_age`, `poi_titles`, `gh_id`, `lh_id`, `org_id`) VALUES (12 , 'Melisandre', NULL, 'Hailing from Asshai, Melisandre is a Red Priestess of the Lord Of Light who resurrects Jon Snow after his murder by multiple members of the Night\'s Watch. ', 401, NULL, NULL, NULL, NULL);
INSERT INTO `gameOfThrones`.`person_of_interest` (`poi_id`, `poi_fname`, `poi_lname`, `poi_desc`, `poi_age`, `poi_titles`, `gh_id`, `lh_id`, `org_id`) VALUES (13, 'Doran', 'Martell', 'Former Prince of Dorne, Lord of Sunspear, and Head of House Martell. Was a very pensive and calculating man. Suffered from an extreme case of gout which restricted him to a wheelchair', NULL, 'Prince of Dorne, Lord of Sunspear', 9, NULL, NULL);
INSERT INTO `gameOfThrones`.`person_of_interest` (`poi_id`, `poi_fname`, `poi_lname`, `poi_desc`, `poi_age`, `poi_titles`, `gh_id`, `lh_id`, `org_id`) VALUES (14, 'Oberyn ', 'Martell', 'Former Prince of Dorne, Oberyn was the younger brother of Doran. Extremely accomplished fighter and master of poison. Studied as a maester for some time and forged several links in a maester\'s chain before growing bored and abandoning his studies. ', NULL, 'Prince of Dorne', 9, NULL, NULL);
