-- -----------------------------------------------------
START TRANSACTION;
USE `gameOfThrones`;
INSERT INTO `gameOfThrones`.`religion` (`relig_code`, `relig_name`, `relig_desc`) VALUES (1, 'Old Gods of the Forest', 'A collection of innumerable and unnamed spritis of nature, which are worshiped by many people of the North and small numbers throughout Westeros');
INSERT INTO `gameOfThrones`.`religion` (`relig_code`, `relig_name`, `relig_desc`) VALUES (2, 'Faith of the Seven', 'The majority religion of Westeros. Based on the the worship of a single deity with seven \'aspects\'/\'faces\'');
INSERT INTO `gameOfThrones`.`religion` (`relig_code`, `relig_name`, `relig_desc`) VALUES (3, 'Drowned God', 'Local religion of the people of the Iron Islands. Centered around maritime skill and seafaring ability. Killing enemies in battle is considered a pious act.');
INSERT INTO `gameOfThrones`.`religion` (`relig_code`, `relig_name`, `relig_desc`) VALUES (4, 'R\'hllor the Lord of Light', 'A deity widely worshipped on the continent of Essos. Centered around the belief in the existence of two deities: R\'hllor the God of Fire and the Great Other who is the god of darkness. Followers believe R\'hllor is the \'one true god\'');
INSERT INTO `gameOfThrones`.`religion` (`relig_code`, `relig_name`, `relig_desc`) VALUES (5, 'Many-Faced God of Death', 'Deity worshipped by the assassins known as the Faceless Men. Its followers believe that Death is unknowingly worshipped  by devotees of all religions in the world just under different names.');
INSERT INTO `gameOfThrones`.`religion` (`relig_code`, `relig_name`, `relig_desc`) VALUES (6, 'Great Stallion', 'A horse god worshipped by the Dothraki. The Dothraki believe that every nation/people have their own idols/gods but the Great Stallion is the greatest of them all.');
INSERT INTO `gameOfThrones`.`religion` (`relig_code`, `relig_name`, `relig_desc`) VALUES (7, 'Great Shepherd', 'The deity worshipped by the Lhazareen. Followers believe that all men are considered part of a single flock.');
INSERT INTO `gameOfThrones`.`religion` (`relig_code`, `relig_name`, `relig_desc`) VALUES (8, 'Ghiscari Religion', 'The religion is followed in Slaver\'s Bay and is centered around Priestesses known as Graces.');
INSERT INTO `gameOfThrones`.`religion` (`relig_code`, `relig_name`, `relig_desc`) VALUES (9, 'Bearded Priests of Norvos', 'The religious, political and military leaders of the Free City of Norvos. They serve as human representatives of Norvos\'s deity but are most famour as warrior-priests. The name of the deity that they worship is secret and only revealed to initiates.');
INSERT INTO `gameOfThrones`.`religion` (`relig_code`, `relig_name`, `relig_desc`) VALUES (10, 'Black Goat of Qohor', 'A deity worshipped in the Free City of Qohor.');

COMMIT;


